function sumEven(arr) {
  return arr.filter(n => n % 2 === 0).reduce((a, b) => a + b, 0);
}

function swapCase(str) {
  return str.split('').map(c => c === c.toUpperCase() ? c.toLowerCase() : c.toUpperCase()).join('');
}

function average(arr) {
  return arr.reduce((a, b) => a + b, 0) / arr.length;
}

function findElement(arr, el) {
  return arr.includes(el);
}

function mergeArrays(a, b) {
  return [...new Set([...a, ...b])];
}

function cToF(c) {
  return (c * 9/5) + 32;
}

function factorial(n) {
  if (n === 0) return 1;
  return n * factorial(n - 1);
}

function isPrime(n) {
  if (n < 2) return false;
  for (let i = 2; i <= Math.sqrt(n); i++) {
    if (n % i === 0) return false;
  }
  return true;
}

function starPattern(n) {
  for (let i = 1; i <= n; i++) {
    console.log('*'.repeat(i));
  }
}

function dateDiff(d1, d2) {
  const diff = Math.abs(new Date(d2) - new Date(d1));
  return Math.ceil(diff / (1000 * 60 * 60 * 24));
}

function numberToWords(n) {
  const words = ['zero','one','two','three','four','five','six','seven','eight','nine'];
  return n.toString().split('').map(d => words[+d]).join(' ');
}

function uuid() {
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, c => {
    const r = Math.random() * 16 | 0;
    const v = c === 'x' ? r : (r & 0x3 | 0x8);
    return v.toString(16);
  });
}

function parseCSV(str) {
  return str.split(',').map(s => s.trim());
}

function simpleHash(str) {
  let hash = 0;
  for (let i = 0; i < str.length; i++) {
    hash += str.charCodeAt(i);
  }
  return hash;
}

function toRoman(num) {
  const val = [1000, 900, 500, 400, 100, 90, 50, 40, 10, 9, 5, 4, 1];
  const sym = ['M','CM','D','CD','C','XC','L','XL','X','IX','V','IV','I'];
  let res = '';
  for (let i = 0; i < val.length; i++) {
    while (num >= val[i]) {
      num -= val[i];
      res += sym[i];
    }
  }
  return res;
}

function compress(str) {
  return str.replace(/(.)\1+/g, (m, c) => c + m.length);
}

function area(w, h) {
  return w * h;
}

function generatePassword(len) {
  const chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
  let pwd = '';
  for (let i = 0; i < len; i++) {
    pwd += chars.charAt(Math.floor(Math.random() * chars.length));
  }
  return pwd;
}

function decToBin(n) {
  return n.toString(2);
}

function binToDec(bin) {
  return parseInt(bin, 2);
}

function fibonacci(n) {
  let seq = [0, 1];
  for (let i = 2; i < n; i++) {
    seq.push(seq[i - 1] + seq[i - 2]);
  }
  return seq.slice(0, n);
}

function toPigLatin(word) {
  return word.slice(1) + word[0] + 'ay';
}

function compoundInterest(p, r, t, n) {
  return p * Math.pow((1 + r/n), n*t);
}

function multiplicationTable(n) {
  for (let i = 1; i <= 10; i++) {
    console.log(`${n} x ${i} = ${n*i}`);
  }
}
